mapper.select:
SELECT COUNT(*)
FROM BOARD
WHERE U_SEQ = #{u_seq}

SELECT COUNT(*)
FROM REPLY
WHERE U_SEQ = #{u_seq}

mapper.update:
UPDATE BOARD
SET
	B_TITLE = #{b_title},
	B_CONTENT = #{b_content},
	B_STATUS = #{b_status}
WHERE B_SEQ = #{b_seq}

UPDATE REPLY
SET
	R_CONTENT = #{r_content}
WHERE R_SEQ = #{r_seq}

UPDATE NOTICE SET
	N_COUNT = N_COUNT + 1
WHERE N_SEQ = #{n_seq }
