mapper.update:
UPDATE GPX
SET G_TITLE = #{g_title},
G_CONTENT = #{g_content},
G_ORI = #{g_ori},
G_RE = #{g_re}
WHERE G_SEQ = #{g_seq}

UPDATE GPXREPLY
SET GR_CONTENT = #{gr_content}
WHERE GR_SEQ = #{gr_seq}

UPDATE GPX
SET G_COUNT = G_COUNT+1
WHERE G_SEQ = #{g_seq}

UPDATE GPX
SET G_RECOMMAND = G_RECOMMAND+1
WHERE G_SEQ = #{g_seq}

UPDATE GPX_RANKING_COURSE
SET 
	GRC_TITLE = #{grc_title },
	GRC_CONTENT = #{grc_content },
	GRC_STATUS = #{grc_status }
WHERE GRC_SEQ = #{grc_seq }

mapper.select:
SELECT *
FROM GPX_USER_RANKING
WHERE U_SEQ = #{u_seq }
	AND GRC_SEQ = #{grc_seq }

SELECT * FROM (
	SELECT ROWNUM AS RANKING, A.*
	FROM (
		SELECT GUR.*, UI.U_NICKNAME
		FROM GPX_USER_RANKING GUR
		JOIN USER_INFO UI
		ON (UI.U_SEQ = GUR.U_SEQ)
		WHERE GRC_SEQ = #{grc_seq }
		ORDER BY GUR_TIME
	) A
) WHERE U_SEQ = #{u_seq }
